function changeimg(n)
{
if (n > 0) {    //check if the slideshow is loaded at the first time.
adNum=n;
}else {
adNum=n + 1;	
}
thisPic = adNum;   //set the position of the image
try
{
window.clearInterval(theTimer);
}
catch (e){}
adNum=adNum-1;
nextAd();
if (n > 0 && stop == false) {   //check if the slideshow is paused already, otherwise, stop it.
stopTran();
}
}
function goUrl()
{
window.open(imgLink[adNum],'_self');
}
var count=0;
for (i=1;i<imgUrl.length;i++)
{
if( (imgUrl[i]!="") && (imgLink[i]!="")&&(imgtext[i]!="")&&(imgAlt[i]!="") ) {
count++;
} else {
break;
}
}
function playTran()
{
if (document.all)
imgInit.filters.revealTrans.play();
}
function   stopTran()
{
if(!stop)
{
if   (document.all)
document.getElementById("imgInit").filters.revealTrans.stop();
clearTimeout(theTimer);
stop=true;
document.getElementById("btnPause").src= "/common/images/icons/play_grey_icon.png";
document.getElementById("btnPause").alt= "play";
}
else
{
stop=false;
document.getElementById("btnPause").src= "/common/images/icons/pause_grey_icon.png";
document.getElementById("btnPause").alt= "pause";
nextAd();
}
}
var key=0;
function nextAd()
{
if(adNum<count)adNum++ ;
else adNum=1;
if( key==0 ){
key=1;
} else if (document.all){
imgInit.filters.revealTrans.Transition=7;
imgInit.filters.revealTrans.apply();
playTran();
}
document.images.imgInit.src=imgUrl[adNum];
document.images.imgInit.alt=imgAlt[adNum];
//document.getElementById('link'+adNum).style.background=buttonLineOn;
document.getElementById('link'+adNum).className = buttonLineOnStyle;
for (var i=1;i<=count;i++)
{if (i!=adNum){
//document.getElementById('link'+i).style.background=buttonLineOff;
document.getElementById('link'+i).className = '';
}
}
document.getElementById('focustext').innerHTML=imgtext[adNum];
if (!stop) {       //check if the control is set to play.
theTimer=setTimeout("nextAd()", TimeOut);
}
else         //if pause, clear the timer.
{
clearTimeout(theTimer);
}
}
document.write('<div id="photo">');

document.write('<a href="javascript:goUrl();"><img style="FILTER: revealTrans(duration=1,transition=5);" width="100%" name="imgInit" ID="imgInit" class="imgClass" border="0"></a></div>');
document.write('<div ID="setnum" style="display:Block" >');
document.write('<div id="controls"><a href="javascript:stopTran();"><img src="/common/images/icons/pause_grey_icon.png" width="20" height="20" border="0" id="btnPause" alt="pause" /></a></div>');
document.write('<ol ID="new">');
for(var i=1;i<imgUrl.length;i++)
{
var linkID=1+i;
document.write('<li><a id="link'+i+'"  href="javascript:changeimg('+i+')" style="cursor:hand" title="'+imgAlt[i]+'" onFocus="this.blur()">&#9679;</a></li>');
}
document.write('</ol >');
document.write('</div>');
document.write('<div  class="caption"><a href="javascript:goUrl();" style="cursor:hand" id="focustext">'+imgtext[i]+'</a></div>');
changeimg('0');
document.onkeydown = keyHit;
var imgCt = imgUrl.length - 1;
if (document.layers) {
		document.captureEvents(Event.KEYDOWN);
		ltArrow = 28;
		rtArrow = 29;
	}
	else {
		ltArrow = 37;
		rtArrow = 39;
	}
	function keyHit(evt) {
		if (evt) {
			thisKey = evt.which;
		}
		else {
		thisKey = window.event.keyCode;
		}
		if (thisKey == ltArrow) {
			chgSlide(-1);
		}
		else if (thisKey == rtArrow) {
			chgSlide(1);
		}
	}
	function chgSlide(direction) {
		if (document.images) {
			thisPic = thisPic + direction;
			if (thisPic > imgCt) {
				thisPic = 1;
			}
			if (thisPic < 1) {
				thisPic = imgCt;
			}
			changeimg(thisPic);
		}
	}
